import { useState, useEffect, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { 
  Users, MessageCircle, DollarSign, Activity, Send, 
  Eye, Clock, Shield, AlertCircle, CheckCircle2, Image, Video, FileText
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface AdminData {
  stats: {
    totalUsers: number;
    subscribedUsers: number;
    onlineUsers: number;
    activeChats: number;
    unreadMessages: number;
    totalRevenue: number;
  };
  users: any[];
  subscriptions: any[];
  activeSessions: any[];
  unreadMessages: any[];
  recentMessages: any[];
}

interface ChatSession {
  id: string;
  user?: any;
  lastMessage?: any;
  unreadCount: number;
  messageCount: number;
  vehicleInfo?: string;
  status: string;
  createdAt: string;
  lastActivity: string;
}

interface Attachment {
  id: string;
  fileName: string;
  originalName: string;
  fileSize: number;
  mimeType: string;
}

interface Message {
  id: string;
  content: string;
  senderType: string;
  sender?: any;
  createdAt: string;
  isRead: boolean;
  attachments?: Attachment[];
}

export default function AdminPanel() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const [selectedChatId, setSelectedChatId] = useState<string | null>(null);
  const [newMessage, setNewMessage] = useState("");
  const [activeTab, setActiveTab] = useState("dashboard");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Check authentication status on mount
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await apiRequest("GET", "/api/admin/dashboard");
        if (response.ok) {
          setIsAuthenticated(true);
        }
      } catch (error) {
        setIsAuthenticated(false);
      } finally {
        setIsLoading(false);
      }
    };

    checkAuthStatus();
  }, []);

  // Login mutation
  const loginMutation = useMutation({
    mutationFn: async (creds: { email: string; password: string }) => {
      const response = await apiRequest("POST", "/api/admin/login", creds);
      return response.json();
    },
    onSuccess: () => {
      setIsAuthenticated(true);
      toast({
        title: "Login successful",
        description: "Welcome to admin panel",
      });
    },
    onError: () => {
      toast({
        title: "Login failed", 
        description: "Invalid credentials",
        variant: "destructive",
      });
    },
  });

  // Dashboard data query
  const { data: dashboardData, refetch: refetchDashboard } = useQuery<AdminData>({
    queryKey: ["/api/admin/dashboard"],
    enabled: isAuthenticated,
    refetchInterval: 5000, // Refresh every 5 seconds
  });

  // Chat sessions query
  const { data: chatSessions } = useQuery<ChatSession[]>({
    queryKey: ["/api/admin/chats"],
    enabled: isAuthenticated,
    refetchInterval: 3000, // Refresh every 3 seconds
  });

  // Messages for selected chat
  const { data: messages, refetch: refetchMessages } = useQuery<Message[]>({
    queryKey: ["/api/admin/chats", selectedChatId, "messages"],
    enabled: isAuthenticated && !!selectedChatId,
    refetchInterval: 2000, // Refresh every 2 seconds
  });

  // Live data query
  const { data: liveData } = useQuery<{
    unreadCount: number;
    activeChatsCount: number;
    onlineUsersCount: number;
    lastUpdate: string;
  }>({
    queryKey: ["/api/admin/live-data"],
    enabled: isAuthenticated,
    refetchInterval: 1000, // Refresh every second
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async ({ sessionId, content }: { sessionId: string; content: string }) => {
      const response = await apiRequest("POST", `/api/admin/chats/${sessionId}/messages`, { content });
      return response.json();
    },
    onSuccess: () => {
      setNewMessage("");
      refetchMessages();
      refetchDashboard();
    },
  });

  // Mark message as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (messageId: string) => {
      const response = await apiRequest("PATCH", `/api/admin/messages/${messageId}/read`);
      return response.json();
    },
    onSuccess: () => {
      refetchMessages();
      refetchDashboard();
    },
  });

  // Logout mutation
  const logoutMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/admin/logout");
      return response.json();
    },
    onSuccess: () => {
      setIsAuthenticated(false);
      setCredentials({ email: "", password: "" });
      toast({
        title: "Logged out successfully",
        description: "See you!",
      });
    },
    onError: () => {
      toast({
        title: "Logout error",
        description: "Please try again",
        variant: "destructive",
      });
    },
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (credentials.email && credentials.password) {
      loginMutation.mutate(credentials);
    }
  };

  const handleSendMessage = () => {
    if (newMessage.trim() && selectedChatId) {
      sendMessageMutation.mutate({
        sessionId: selectedChatId,
        content: newMessage.trim(),
      });
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString("en-US");
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType.startsWith('image/')) return <Image className="w-4 h-4" />;
    if (mimeType.startsWith('video/')) return <Video className="w-4 h-4" />;
    return <FileText className="w-4 h-4" />;
  };

  const renderAttachment = (attachment: Attachment) => {
    const isImage = attachment.mimeType.startsWith('image/');
    const isVideo = attachment.mimeType.startsWith('video/');

    if (isImage) {
      return (
        <div className="mt-2">
          <img 
            src={`/api/uploads/${attachment.fileName}`}
            alt={attachment.originalName}
            className="max-w-xs rounded-lg border border-white/20"
            style={{ maxHeight: '200px' }}
          />
          <p className="text-xs opacity-70 mt-1">
            {attachment.originalName} ({formatFileSize(attachment.fileSize)})
          </p>
        </div>
      );
    }

    if (isVideo) {
      return (
        <div className="mt-2">
          <video 
            src={`/api/uploads/${attachment.fileName}`}
            controls
            className="max-w-xs rounded-lg border border-white/20"
            style={{ maxHeight: '200px' }}
          />
          <p className="text-xs opacity-70 mt-1">
            {attachment.originalName} ({formatFileSize(attachment.fileSize)})
          </p>
        </div>
      );
    }

    return (
      <div className="mt-2 p-2 border border-white/20 rounded-lg flex items-center space-x-2 bg-white/10">
        {getFileIcon(attachment.mimeType)}
        <div className="flex-1">
          <p className="text-sm font-medium">{attachment.originalName}</p>
          <p className="text-xs opacity-70">{formatFileSize(attachment.fileSize)}</p>
        </div>
      </div>
    );
  };

  const getVehicleInfo = (vehicleInfoString?: string) => {
    try {
      return vehicleInfoString ? JSON.parse(vehicleInfoString) : null;
    } catch {
      return null;
    }
  };

  // Play notification sound for new user messages
  const prevMessagesCount = useRef(messages?.length || 0);
  useEffect(() => {
    if (messages && messages.length > prevMessagesCount.current) {
      const newMessages = messages.slice(prevMessagesCount.current);
      const hasUserMessage = newMessages.some(msg => msg.senderType === "user");

      if (hasUserMessage) {
        // Play notification sound
        const audio = new Audio('data:audio/wav;base64,');
        audio.volume = 0.3;
        audio.play().catch(err => console.log('Audio play failed:', err));
      }
    }
    prevMessagesCount.current = messages?.length || 0;
  }, [messages]);

  // Auto-mark messages as read when viewing chat
  useEffect(() => {
    if (messages && selectedChatId) {
      const unreadMessages = messages.filter(
        (msg) => !msg.isRead && msg.senderType === "user"
      );
      unreadMessages.forEach((msg) => {
        markAsReadMutation.mutate(msg.id);
      });
    }
  }, [messages, selectedChatId]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Checking permissions...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-96">
          <CardHeader>
            <CardTitle className="text-center">Admin Panel</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Email:</label>
                <Input
                  type="email"
                  value={credentials.email}
                  onChange={(e) =>
                    setCredentials({ ...credentials, email: e.target.value })
                  }
                  placeholder="Enter admin email"
                  data-testid="input-admin-email"
                />
              </div>
              <div>
                <label className="text-sm font-medium">Password:</label>
                <Input
                  type="password"
                  value={credentials.password}
                  onChange={(e) =>
                    setCredentials({ ...credentials, password: e.target.value })
                  }
                  placeholder="Enter admin password"
                  data-testid="input-admin-password"
                />
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={loginMutation.isPending}
                data-testid="button-admin-login"
              >
                {loginMutation.isPending ? "Logging in..." : "Log In"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="border-b bg-card/50 backdrop-blur">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold">Admin Panel</h1>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-sm">
                <div className="w-2 h-2 bg-success rounded-full" />
                <span>Online</span>
              </div>
              <Badge variant="secondary">
                {liveData?.unreadCount || 0} unread
