client/src/components/LoginModal.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";

interface LoginModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onLoginSuccess?: () => void;
}

export default function LoginModal({ open, onOpenChange, onLoginSuccess }: LoginModalProps) {
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [registerData, setRegisterData] = useState({ 
    username: "", 
    email: "", 
    password: "",
    confirmPassword: ""
  });
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Login mutation
  const loginMutation = useMutation({
    mutationFn: async (data: { email: string; password: string }) => {
      const response = await apiRequest("POST", "/api/users/login", data);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/users/me'] });
      toast({
        title: "Login successful",
        description: `Welcome back, ${data.user?.username || 'User'}!`,
      });
      onOpenChange(false);
      setLoginData({ email: "", password: "" });
      onLoginSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Login failed",
        description: "Invalid email or password",
        variant: "destructive",
      });
    },
  });

  // Register mutation
  const registerMutation = useMutation({
    mutationFn: async (data: { username: string; email: string; password: string }) => {
      const response = await apiRequest("POST", "/api/users/register", data);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/users/me'] });
      toast({
        title: "Account created successfully",
        description: `Welcome to ChatWithMechanic, ${data.username}!`,
      });
      onOpenChange(false);
      setRegisterData({ username: "", email: "", password: "", confirmPassword: "" });
      onLoginSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Registration failed",
        description: "User might already exist or invalid data provided",
        variant: "destructive",
      });
    },
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (!loginData.email || !loginData.password) {
      toast({
        title: "Missing information",
        description: "Please fill in all fields",
        variant: "destructive",
      });
      return;
    }
    loginMutation.mutate(loginData);
  };

  const handleRegister = (e: React.FormEvent) => {
    e.preventDefault();
    if (!registerData.username || !registerData.email || !registerData.password) {
      toast({
        title: "Missing information",
        description: "Please fill in all fields",
        variant: "destructive",
      });
      return;
    }
    if (registerData.password !== registerData.confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }
    if (registerData.password.length < 6) {
      toast({
        title: "Password too short",
        description: "Password must be at least 6 characters long",
        variant: "destructive",
      });
      return;
    }

    const { confirmPassword, ...dataToSend } = registerData;
    registerMutation.mutate(dataToSend);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Welcome to ChatWithMechanic</DialogTitle>
          <DialogDescription>
            Sign in to your account or create a new one to get started with professional automotive advice.
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="login" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="login" data-testid="tab-login">Sign In</TabsTrigger>
            <TabsTrigger value="register" data-testid="tab-register">Sign Up</TabsTrigger>
          </TabsList>

          <TabsContent value="login" className="space-y-4">
            <form onSubmit={handleLogin} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="login-email">Email</Label>
                <Input
                  id="login-email"
                  type="email"
                  placeholder="Enter your email"
                  value={loginData.email}
                  onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                  data-testid="input-login-email"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="login-password">Password</Label>
                <Input
                  id="login-password"
                  type="password"
                  placeholder="Enter your password"
                  value={loginData.password}
                  onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                  data-testid="input-login-password"
                  required
                />
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={loginMutation.isPending}
                data-testid="button-login-submit"
              >
                {loginMutation.isPending ? "Signing in..." : "Sign In"}
              </Button>
            </form>
          </TabsContent>

          <TabsContent value="register" className="space-y-4">
            <form onSubmit={handleRegister} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="register-username">Username</Label>
                <Input
                  id="register-username"
                  type="text"
                  placeholder="Choose a username"
                  value={registerData.username}
                  onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })}
                  data-testid="input-register-username"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="register-email">Email</Label>
                <Input
                  id="register-email"
                  type="email"
                  placeholder="Enter your email"
                  value={registerData.email}
                  onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
                  data-testid="input-register-email"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="register-password">Password</Label>
                <Input
                  id="register-password"
                  type="password"
                  placeholder="Create a password (min. 6 characters)"
                  value={registerData.password}
                  onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
                  data-testid="input-register-password"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="register-confirm-password">Confirm Password</Label>
                <Input
                  id="register-confirm-password"
                  type="password"
                  placeholder="Confirm your password"
                  value={registerData.confirmPassword}
                  onChange={(e) => setRegisterData({ ...registerData, confirmPassword: e.target.value })}
                  data-testid="input-register-confirm-password"
                  required
                />
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={registerMutation.isPending}
                data-testid="button-register-submit"
              >
                {registerMutation.isPending ? "Creating account..." : "Create Account"}
              </Button>
            </form>
          </TabsContent>
        </Tabs>

        <Separator />

        <div className="text-center text-sm text-muted-foreground">
          <p>After creating an account, you can purchase chat access for $9.99 to get unlimited automotive advice from certified mechanics.</p>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ChatHistory.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { MessageCircle, Clock, User, Wrench } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface ChatSession {
  id: string;
  userId: string;
  vehicleInfo: string;
  status: string;
  createdAt: string;
  lastActivity: string;
  lastMessage: {
    content: string;
    createdAt: string;
    senderType: string;
  } | null;
  messageCount: number;
  unreadCount: number;
}

interface ChatHistoryProps {
  onSelectSession: (sessionId: string, vehicleInfo: any) => void;
  onStartNewChat: () => void;
}

export default function ChatHistory({ onSelectSession, onStartNewChat }: ChatHistoryProps) {
  const { data: sessions = [], isLoading, error } = useQuery<ChatSession[]>({
    queryKey: ["/api/chat/sessions"],
    refetchOnWindowFocus: false,
  });

  const parseVehicleInfo = (vehicleInfoJson: string) => {
    try {
      return JSON.parse(vehicleInfoJson || '{}');
    } catch {
      return {};
    }
  };

  const getVehicleDisplayName = (vehicleInfo: any) => {
    if (!vehicleInfo || Object.keys(vehicleInfo).length === 0) {
      return "General Consultation";
    }

    const { year, make, model, type } = vehicleInfo;
    const parts = [year, make, model].filter(Boolean);

    if (parts.length > 0) {
      return parts.join(" ");
    }

    return type || "Vehicle Consultation";
  };

  const truncateMessage = (content: string, maxLength: number = 60) => {
    if (!content) return "";
    return content.length > maxLength ? content.substring(0, maxLength) + "..." : content;
  };

  const getSenderIcon = (senderType: string) => {
    switch (senderType) {
      case "admin":
        return <Wrench className="w-4 h-4" />;
      case "user":
        return <User className="w-4 h-4" />;
      default:
        return <MessageCircle className="w-4 h-4" />;
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5" />
            Your Chat History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="h-20 bg-muted rounded-lg"></div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5" />
            Your Chat History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-6">
            <p className="text-muted-foreground mb-4">
              Unable to load chat history
            </p>
            <Button onClick={onStartNewChat} data-testid="button-start-new-chat">
              Start New Chat
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (sessions.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5" />
            Your Chat History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-6">
            <MessageCircle className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="font-semibold mb-2">No previous chats</h3>
            <p className="text-muted-foreground mb-4">
              Start your first consultation with our mechanics
            </p>
            <Button onClick={onStartNewChat} data-testid="button-start-new-chat">
              Start New Chat
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5" />
            Your Chat History
          </CardTitle>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={onStartNewChat}
            data-testid="button-start-new-chat"
          >
            New Chat
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {sessions.map((session) => {
            const vehicleInfo = parseVehicleInfo(session.vehicleInfo);
            const vehicleDisplayName = getVehicleDisplayName(vehicleInfo);

            return (
              <Card 
                key={session.id} 
                className="hover-elevate cursor-pointer transition-all"
                onClick={() => onSelectSession(session.id, vehicleInfo)}
                data-testid={`chat-session-${session.id}`}
              >
                <CardContent className="p-4">
                  <div className="flex justify-between items-start mb-2">
                    <h4 className="font-medium text-sm">
                      {vehicleDisplayName}
                    </h4>
                    <div className="flex items-center gap-2">
                      {session.unreadCount > 0 && (
                        <Badge 
                          variant="destructive" 
                          className="text-xs px-2 py-1"
                          data-testid={`badge-unread-${session.id}`}
                        >
                          {session.unreadCount}
                        </Badge>
                      )}
                      <Badge 
                        variant={session.status === "active" ? "default" : "secondary"}
                        className="text-xs"
                        data-testid={`badge-status-${session.id}`}
                      >
                        {session.status}
                      </Badge>
                    </div>
                  </div>

                  {session.lastMessage && (
                    <div className="flex items-center gap-2 mb-2">
                      {getSenderIcon(session.lastMessage.senderType)}
                      <p className="text-sm text-muted-foreground flex-1">
                        {truncateMessage(session.lastMessage.content)}
                      </p>
                    </div>
                  )}

                  <div className="flex items-center justify-between text-xs text-muted-foreground">
                    <div className="flex items-center gap-1">
                      <Clock className="w-3 h-3" />
                      {formatDistanceToNow(new Date(session.lastActivity), { addSuffix: true })}
                    </div>
                    <div className="flex items-center gap-1">
                      <MessageCircle className="w-3 h-3" />
                      {session.messageCount} messages
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

